import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { TextField, Button } from '@mui/material';

function ProductForm() {
  const { id } = useParams(); // if id exists, we are editing; otherwise, creating
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    stock: '',
    imageUrl: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    if (id) {
      axios.get(`http://localhost:3305/products/${id}`)
        .then(response => {
          setFormData(response.data);
        })
        .catch(error => console.error('Error fetching product:', error));
    }
  }, [id]);

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (id) {
      // Update product
      axios.put(`http://localhost:3305/products/${id}`, formData)
        .then(response => {
          alert('Product updated!');
          navigate('/');
        })
        .catch(error => console.error('Error updating product:', error));
    } else {
      // Create product
      axios.post(`http://localhost:3305/products`, formData)
        .then(response => {
          alert('Product created!');
          navigate('/');
        })
        .catch(error => console.error('Error creating product:', error));
    }
  };

  return (
    <div>
      <h1>{id ? 'Edit Product' : 'New Product'}</h1>
      <form onSubmit={handleSubmit}>
        <TextField
          name="name"
          label="Name"
          value={formData.name}
          onChange={handleChange}
          required
          fullWidth
          margin="normal"
        />
        <TextField
          name="description"
          label="Description"
          value={formData.description}
          onChange={handleChange}
          required
          fullWidth
          margin="normal"
          multiline
          rows={4}
        />
        <TextField
          name="price"
          label="Price"
          type="number"
          value={formData.price}
          onChange={handleChange}
          required
          fullWidth
          margin="normal"
        />
        <TextField
          name="stock"
          label="Stock"
          type="number"
          value={formData.stock}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <TextField
          name="imageUrl"
          label="Image URL"
          value={formData.imageUrl}
          onChange={handleChange}
          fullWidth
          margin="normal"
        />
        <Button variant="contained" type="submit" style={{ marginTop: '20px' }}>
          {id ? 'Update Product' : 'Create Product'}
        </Button>
      </form>
    </div>
  );
}

export default ProductForm;
