import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Rating, Button, TextField } from '@mui/material';

function ProductDetail() {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [newRating, setNewRating] = useState(0);
  const [comment, setComment] = useState('');

  // Fetch product including its ratings
  useEffect(() => {
    axios.get(`http://localhost:3305/products/${id}`)
      .then(response => setProduct(response.data))
      .catch(error => console.error('Error fetching product:', error));
  }, [id]);

  // Calculate average rating if ratings exist
  const averageRating = product && product.ratings && product.ratings.length > 0
    ? product.ratings.reduce((sum, r) => sum + r.rating, 0) / product.ratings.length
    : 0;

  const handleRatingSubmit = () => {
    axios.post(`http://localhost:3305/products/${id}/addRating`, { rating: newRating, comment })
      .then(response => {
        alert('Rating submitted!');
        // Refresh the product details to include the new rating
        return axios.get(`http://localhost:3305/products/${id}`);
      })
      .then(response => setProduct(response.data))
      .catch(error => console.error('Error submitting rating:', error));
  };

  const handleAddToCart = () => {
    // For demo purposes, we use a static userId (e.g., 1) and quantity 1.
    axios.post(`http://localhost:3305/cart/addProduct`, { userId: 1, productId: id, quantity: 1 })
      .then(response => alert('Product added to cart!'))
      .catch(error => console.error('Error adding to cart:', error));
  };

  if (!product) return <div>Loading...</div>;

  return (
    <div>
      <h1>{product.name}</h1>
      {product.imageUrl && <img src={product.imageUrl} alt={product.name} style={{ maxWidth: '300px' }} />}
      <p>{product.description}</p>
      <p>Price: ${product.price}</p>
      
      <h3>Average Rating: {averageRating.toFixed(1)}</h3>
      <Rating value={averageRating} precision={0.1} readOnly />

      <h3>Ratings:</h3>
      {product.ratings && product.ratings.length > 0 ? (
        product.ratings.map((r) => (
          <div key={r.id}>
            <Rating value={r.rating} readOnly />
            {r.comment && <p>{r.comment}</p>}
          </div>
        ))
      ) : (
        <p>No ratings yet.</p>
      )}

      <div style={{ marginTop: '20px' }}>
        <h3>Rate this product:</h3>
        <Rating
          value={newRating}
          onChange={(e, newValue) => setNewRating(newValue)}
        />
        <TextField
          label="Comment (optional)"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
          multiline
          rows={2}
          variant="outlined"
          style={{ marginTop: '10px', display: 'block' }}
        />
        <Button variant="contained" onClick={handleRatingSubmit} style={{ marginTop: '10px' }}>
          Submit Rating
        </Button>
      </div>

      <Button variant="contained" onClick={handleAddToCart} style={{ marginTop: '20px' }}>
        Add to Cart
      </Button>
    </div>
  );
}

export default ProductDetail;
